export function hexp(hexpEl){const[tag,attrs={},...content]=hexpEl;const isComponent=typeof tag==="function";if(isComponent)return hexp(tag(attrs,content));const el=document.createElement(tag.toLowerCase());Object.entries(attrs).forEach(([name,value])=>{const prefix=name.slice(0,2).toLowerCase();const isEvent=prefix==="on";if(isEvent)addEvent(el,name,value);else if(isObject(value))addObjectAttr(el,name,value);else el.setAttribute(name,value)});content.forEach(hexpChild=>{if(isString(hexpChild))el.appendChild(document.createTextNode(hexpChild));else if(isArray(hexpChild))el.appendChild(hexp(hexpChild))});return el}function addEvent(el,name,value){const eventName=name.slice(2).toLowerCase();el.addEventListener(eventName,value)}function addObjectAttr(el,name,value){const objValue=Object.entries(value).map(([k,v])=>`${k}:${v};`).join("");el.setAttribute(name,objValue)}const isArray=maybe=>Array.isArray(maybe);const isString=maybe=>typeof maybe==="string";const isObject=maybe=>maybe instanceof Object;